// Expecting quotes to be an array, but the API might wrap it.
// Start from the raw payload.
let quotes = msg.payload;

// 1) Unwrap common patterns if it's not directly an array
if (!Array.isArray(quotes)) {
    if (Array.isArray(quotes.result)) {
        quotes = quotes.result;
    } else if (Array.isArray(quotes.data)) {
        quotes = quotes.data;
    } else if (Array.isArray(quotes.quotes)) {
        quotes = quotes.quotes;
    } else {
        // Log what we actually got, to see the shape in the debug sidebar
        node.warn("format order line: payload is not an array: " + JSON.stringify(msg.payload));
        return msg;  // keep original so you can inspect it
    }
}

// 2) For each quote, build a nice "order_line_products" string
quotes = quotes.map(q => {
    let lines = q.order_line;

    // Sometimes order_line might be a JSON string â€” try to parse if so
    if (typeof lines === "string") {
        try {
            lines = JSON.parse(lines);
        } catch (e) {
            lines = [];
        }
    }

    if (!Array.isArray(lines)) {
        lines = [];
    }

    const productNames = lines
        .map(l => l.name || "")
        .filter(n => n)               // drop empty names
        .join(", ");

    return {
        ...q,
        order_line: lines,            // keep the original structure for the detail table
        order_line_products: productNames  // new summary field for the main table
    };
});

// 3) Put it back into msg.payload for the ui-table
msg.payload = quotes;
return msg;

// Expecting msg.payload to look like:
// { customer_id: 14, orders: [ {...}, {...}, ... ] }

let data = msg.payload;
let orders = [];

// If the HTTP response is wrapped differently, unwrap common cases:
if (Array.isArray(data)) {
    // Already an array of orders
    orders = data;
} else if (Array.isArray(data.orders)) {
    // Standard: { customer_id, orders: [...] }
    orders = data.orders;
} else if (data.result && Array.isArray(data.result.orders)) {
    orders = data.result.orders;
} else {
    // Fallback: log what we got so you can inspect in debug
    node.warn("Unexpected deliveries shape: " + JSON.stringify(data));
    msg.payload = data;
    return msg;
}

// Build a *flat* list of orders for the table
const rows = orders.map(o => ({
    order_id: o.order_id,
    order_name: o.order_name,
    order_state: o.order_state,
    date_order: o.date_order,
    delivery_status: o.delivery_status,
    deliveries_count: (o.deliveries || []).length,
    // optional: human-readable list of deliveries (remove if you don't want it)
    deliveries_summary: (o.deliveries || [])
        .map(d => `${d.name} (${d.state})`)
        .join(", ")
}));

msg.payload = rows;
return msg;

let selectedRows = flow.get('selected_products') || [];

function indexById(arr, id) {
    return arr.findIndex(r => r.value === id);
}

if (Array.isArray(msg.payload)) {
    selectedRows = msg.payload;
} else if (msg.payload && typeof msg.payload === 'object') {
    const row = msg.payload;
    const id = row.value;   // ðŸ‘ˆ this MUST stay `value`

    if (id == null) {
        node.warn("Row without 'value' field: " + JSON.stringify(row));
    } else {
        const idx = indexById(selectedRows, id);

        const isSelected = (row._selected !== false);

        if (isSelected) {
            if (idx === -1) {
                selectedRows.push(row);
            }
        } else {
            if (idx !== -1) {
                selectedRows.splice(idx, 1);
            }
        }
    }
}

flow.set('selected_products', selectedRows);
flow.set('selected_product_ids', selectedRows.map(r => r.value));  // ðŸ‘ˆ still `value`

msg.payload = selectedRows;
return msg;

const customerId = flow.get('customer_id');
const productIds = flow.get('selected_product_ids') || [];

if (!customerId) {
    node.error("No customer_id in flow context. Make sure a customer is selected first.");
    return null;
}

if (!productIds.length) {
    node.error("No products selected. Please add items to the cart.");
    return null;
}

// Build JSON body for FastAPI
msg.headers = msg.headers || {};
msg.headers['Content-Type'] = 'application/json';

msg.payload = {
    cust_id: customerId,
    product_ids: productIds
};

return msg;

// Get the quote ID that was saved when user clicked a row in the Quotations table
const quoteId = flow.get('selected_quote_id');

if (!quoteId) {
    node.error("No quotation selected. Please click on a quotation first.");
    return null;   // stop the flow if nothing is selected
}

// Put the ID into msg.payload so the HTTP node can use it in the URL
msg.payload = quoteId;

// (Optional) Set headers â€“ useful if you later send a JSON body
msg.headers = msg.headers || {};
msg.headers['Content-Type'] = 'application/json';

return msg;

// handle invoice click
const row = msg.payload;

if (!row || typeof row !== "object") {
    node.warn("Expected invoice row object, got: " + JSON.stringify(msg.payload));
    return [null, null];
}

// store id if you want
flow.set("selected_invoice_id", row.id);

// Output 1: line details
const msgLines = {
    payload: row.invoice_line_ids
};

// Output 2: just the ID for preview_url endpoint
const msgPreview = {
    payload: row.id
};

return [msgLines, msgPreview];

// We expect something like:
// {
//   "invoice_id": 47,
//   "full_preview_url": "https://...",
//   "raw_odoo_response": { ... }
// }

const p = msg.payload || {};

if (p.full_preview_url) {
    msg.payload = p.full_preview_url;   // make payload = plain URL string
} else {
    node.warn("No full_preview_url in payload: " + JSON.stringify(p));
    msg.payload = JSON.stringify(p);    // at least show *something* if shape is different
}

return msg;
